def createtable():
    import mysql.connector
    mydb=mysql.connector.connect(host='localhost',user='root',passwd='admin',database='hospital_management')
    mycursor=mydb.cursor()
    mycursor.execute("CREATE TABLE DOCTOR_LIST(ID integer NOT NULL PRIMARY KEY,D_NAME char(30) NOT NULL,TYPE char(20) NOT NULL,SPECIALITY char(40) NOT NULL ,AGE integer, ROOMNO int(3) NOT NULL)")
    mycursor.execute("CREATE TABLE DOC_INFO(ID integer NOT NULL PRIMARY KEY,D_NAME char(30) NOT NULL,DOJ DATE NOT NULL,DOB DATE NOT NULL,ADDRESS varchar(50),SALARY integer)")
    mycursor.execute("CREATE TABLE OLD_PATIENT(P_ID integer PRIMARY KEY,P_NAME char(30) NOT NULL,AGE integer NOT NULL,DOA DATE NOT NULL,BILL integer NOT NULL,PROBLEM_or_TREATMENT char(40) NOT NULL,DOC_ASSISTED char(40) NOT NULL)")
    mycursor.execute("CREATE TABLE CURR_PATIENT(P_ID integer PRIMARY KEY,P_NAME char(30) NOT NULL,AGE integer NOT NULL,DOA DATE NOT NULL,TREATEMENT char(40) NOT NULL,ROOM_NO integer )")                                                            
